mapper.select:
SELECT *
FROM ADMINNOTICE
WHERE AIDX =#{aidx}

SELECT * 
FROM USERS
WHERE UIDX= #{uidx}

SELECT COUNT(*)
FROM BUYS 
WHERE UIDX=${uidx} and PDIDX=${didx}

SELECT * 
FROM (
	SELECT ROWNUM as rn,a.*
	FROM (
		SELECT r.* 
		FROM USERS u,BUYS b,REVIEW r
		WHERE u.UIDX=${uidx} and u.UIDX=b.UIDX and b.BIDX = r.BIDX
	)a
) WHERE rn BETWEEN ${beginRecord} AND ${endRecord}

SELECT * 
FROM (
	SELECT ROWNUM as rn,a.*
	FROM (
		SELECT * 
		FROM CUSTOMER_SERVICE
		WHERE UIDX=${uidx} and DIDX=${didx}
	)a
) WHERE rn BETWEEN ${beginRecord} AND ${endRecord}

SELECT count(*) 
FROM CUSTOMER_SERVICE
WHERE UIDX=${uidx} and DIDX=${didx}

mapper.update:
UPDATE USERS
SET UGRADE =${grade}
WHERE UIDX =${uidx}

mapper.select:
SELECT *
FROM BANNER
WHERE BIDX=#{bidx}

mapper.insert:
SELECT POPUPNOTICE_SEQ.NEXTVAL as popIdx FROM DUAL
INSERT INTO POPUPNOTICE( POPIDX, POPIMG, POPINFO, POPURI ) VALUES( ${popIdx}, '${popImg}', '${popInfo}', '${popupUri}'
)

mapper.select:
SELECT * 
FROM POPUPNOTICE
WHERE POPIDX=#{param1}

SELECT *
FROM DEPT
WHERE DIDX =#{didx}

SELECT *
FROM PRODUCTS
WHERE DIDX=#{didx}

		SELECT *
		FROM(
    		SELECT ROWNUM as rn,a.*
    		FROM(
        		SELECT bidx,border_num,total_price as price,border_status
       			FROM BUYS 
        		WHERE DIDX=${didx}
       			ORDER BY BDATE DESC
       			)a
			)WHERE rn between ${beginRecord} and  ${endRecord}

SELECT COUNT(*)
FROM BUYS 
WHERE DIDX=#{didx}

SELECT * 
FROM (
	SELECT ROWNUM as rn,a.*
	FROM (
		SELECT r.* 
		FROM DEPT d,BUYS b,REVIEW r
		WHERE d.DIDX=${didx} and d.dIDX=b.PDIDX and b.BIDX = r.BIDX
	)a
) WHERE rn BETWEEN ${beginRecord} AND ${endRecord}
