mapper.select:
SELECT COUNT(*)
FROM BUYS 
WHERE UIDX=${uidx}

SELECT * 
FROM (
	SELECT ROWNUM as rn,a.*
	FROM (
		SELECT r.* 
		FROM USERS u,BUYS b,REVIEW r
		WHERE u.UIDX=${uidx} and u.UIDX=b.UIDX and b.BIDX = r.BIDX
	)a
) WHERE rn BETWEEN ${beginRecord} AND ${endRecord}

SELECT * 
FROM (
	SELECT ROWNUM as rn,a.*
	FROM (
		SELECT * 
		FROM CUSTOMER_SERVICE
		WHERE UIDX=${uidx}
	)a
) WHERE rn BETWEEN ${beginRecord} AND ${endRecord}

SELECT count(*) 
FROM CUSTOMER_SERVICE
WHERE UIDX=${uidx}

mapper.update:
UPDATE USERS
SET UGRADE =${grade}
WHERE UIDX =${uidx}
